import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import Header from '../../components/header/header';
import { GlobalContext } from '../../context/globalStateContext';
import { ContainerBase } from '../../style';
import { ContainerList, ContainerPage, ContainerEachClient, ClientInfo, ClientAction,ContainerAdd } from './style';

export default function FourPage() {
  const context = useContext(GlobalContext)

  const [inputName, setInputName]= useState('')
  const [inputCPF, setInputCPF]= useState('')
  const [inputEmail, setInputEmail]= useState('')
  const [inputPhone, setInputPhone]= useState('')
  const [inputAddress, setInputAddress]= useState('')

  const body = {
    name: inputName,
    cpf: inputCPF,
    email: inputEmail,
    phone: inputPhone,
    address: inputAddress
  }

  const addClient = (ev)=>{
    ev.preventDefault()
    axios
      .post('http://localhost:3003/clients', body)
      .then((resp)=>{
        alert('Cliente cadastrado com sucesso.')
        document.getElementById('clientAdd').style.display = 'none'
        document.getElementById('clientlist').style.display = 'flex'
      })
      .catch((err)=>{console.log(err.response.data.message)})

  }

 return (
   <ContainerBase>
        <Header/>
        <ContainerPage>
          <ContainerList id='clientList'>
            {context.clients.map((client, key)=>{
              return (
              <ContainerEachClient key={key}>
                <ClientInfo>
                  <span>NOME: {client.name}</span>
                  <span>CPF: {client.cpf}</span>
                  <span>EMAIL: {client.email}</span>
                  <span>TELEFONE: {client.phone}</span>
                  <span>ENDERECO: {client.address}</span>

                </ClientInfo>
                <ClientAction>
                  <button>E</button>
                  <button>X</button>
                </ClientAction>
              </ContainerEachClient>)
            })}
          </ContainerList>
          <ContainerAdd id='clientAdd'>
            <form onSubmit={addClient}>
            <h2>Cadastrar cliente</h2>
              <input
                placeholder='NOME'
                value={inputName}
                onChange={(ev)=>{setInputName(ev.target.value)}}
              />
              <input
                placeholder='CPF'
                value={inputCPF}
                onChange={(ev)=>{setInputCPF(ev.target.value)}}
              />
              <input
                placeholder='EMAIL'
                value={inputEmail}
                onChange={(ev)=>{setInputEmail(ev.target.value)}}
              />
              <input
                placeholder='TELEFONE'
                value={inputPhone}
                onChange={(ev)=>{setInputPhone(ev.target.value)}}
              />
              <input
                placeholder='ENDRECO'
                value={inputAddress}
                onChange={(ev)=>{setInputAddress(ev.target.value)}}
              />

              <button>Cadastrar</button>
            </form>
          </ContainerAdd>
        </ContainerPage>
   </ContainerBase>
 );
}
