import { useState } from 'react'
import {GlobalContext} from '../context/globalStateContext'

export const GlobalState = (props)=>{

    const [clients, setClients] = useState({})

    const data = {
        clients,
        setClients
    }

    return(
        <GlobalContext.Provider value={data}>
            {props.children}
        </GlobalContext.Provider>
    )
}
