import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import Header from '../../components/header/header';
import { GlobalContext } from '../../context/globalStateContext';
import useRequestData from '../../hooks/useRequestdata';
import { ContainerBase } from '../../style';
import { ContainerList, ContainerPage, ContainerEachClient, ClientInfo, ClientAction,ContainerAdd } from './style';
import { URL_BASE } from '../../routes/constants';

export default function FourPage() {
  const context = useContext(GlobalContext)
  const [data] = useRequestData(`${URL_BASE}`)

  const renderClient = data && data.clients.map((client, key)=>{
    return(
      <ContainerEachClient key={key}>
                <ClientInfo>
                  <span>NOME: {client.name}</span>
                  <span>CPF: {client.cpf}</span>
                  <span>EMAIL: {client.email}</span>
                  <span>TELEFONE: {client.phone}</span>
                  <span>ENDERECO: {client.address}</span>

                </ClientInfo>
                <ClientAction>
                  <button>E</button>
                  <button onClick={()=>{removeClient(client._id)}}>X</button>
                </ClientAction>
              </ContainerEachClient>
    )
  })




  const removeClient = (id) => {
      axios
       .delete(`http://localhost:3003/clients/${id}`)
       .then((resp)=>{ alert('Cliente deletado com sucesso...')})
  }

 return (
   <ContainerBase>
        <Header/>
        <ContainerPage>
          <ContainerList>
            {renderClient}
          </ContainerList>
        </ContainerPage>
   </ContainerBase>
 );

}
