import styled from 'styled-components';

export const ContainerPage = styled.div`
    width: 100%;
    height: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
    background: #fff;
`;

export const ContainerList = styled.div`
    width:100%;
    height: 100%;
    border: 2px solid black;
    flex-wrap: wrap;
    overflow-y: auto;
    table{
        width:100%;
        text-align: center;
        tr{
            th{
                width:100px;
                background:#424242;
                color: #fff;
            }
            td{
                button{
                    margin: 0 1rem;
                    :hover{
                        scale: 1.2;
                    }
                }
            }
        }
    }

`
