import styled from 'styled-components';

export const ContainerPage = styled.div`
    width: 100%;
    height: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
    background: red;
`;

export const ContainerAdd = styled.div`
    width: 35%;
    height: 100%;
    border: 2px solid black;
    form{
        width: 100%;
        height: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        input{
            width: 300px;
            height: 30px;
            margin: 1rem 0;
            border-radius: 20px;
        }
        button{
            margin: 1rem 0;
            width: 200px;
            height: 20px;
        }
    }
`

//------------------------------------------------------------------------------------//

export const ContainerEdit = styled.div`
    width: 35%;
    height: 100%;
    border: 2px solid black;
`

//------------------------------------------------------------------------------------//

export const ContainerList = styled.div`
    width:35%;
    height: 100%;
    border: 2px solid black;
    display: none;
    justify-content: center;
    flex-wrap: wrap;
`

export const ContainerEachClient = styled.div`
    width: 300px;
    height: 300px;
    border: 2px solid black;
    display: flex;
    flex-direction: column;
    margin: 0 1rem;
`

export const ClientInfo = styled.div`
    width: 100%;
    height: 80%;
    background: white;
    display: flex;
    flex-direction: column;
    span{
        margin: 10px 0;
    }
`

export const ClientAction = styled.div`
    width: 100%;
    height: 20%;
    background: black;
    display: flex;
    justify-content: center;
    align-items: center;
    button{
        width: 40px;
        height:40px;
        border-radius:50%;
        margin: 0 2rem;
    }
`
