import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { RandonUserApi } from '../../routes/constants';
import { ContainerBase } from '../../style';
import { ContainerPage, ContainerAction, ContainerSearch, ContainerUser } from './style';

export default function FirstPage() {

   const [renderUser, setRenderUser] = useState([])
   const [inputSearch, setInputSearch] = useState('')
   const navigate = useNavigate('')
   const [page, setPage] = useState(1)
   useEffect(()=>{ 
      axios
         .get(`https://randomuser.me/api/?page=${page}&results=5&seed=abc`)
         .then((resp)=>{
            setRenderUser(resp.data.results) 
            console.log(resp.data);
         })
   },[page])

   const nextPage = ()=>{
      navigate('/secondPage')
   }

   const nextResults = () =>{
      setPage(page + 1)
   }

   const previousResults = () =>{
      if(page > 1){
         setPage(page - 1)
      }
   }
 return (
   <ContainerBase>
      <ContainerSearch>
         <input
            placeholder='Pesquisar'
            value={inputSearch}
            onChange={(ev)=>{ setInputSearch(ev.target.value)}}
         />
         <button onClick={previousResults}>Anterior</button>
         <button onClick={nextResults}>Proxima</button>
         <button onClick={nextPage}>Avancar</button>
      </ContainerSearch>
      <ContainerPage>

      </ContainerPage>
   </ContainerBase>
 );
}
