import styled from 'styled-components';

export const ContainerPage = styled.div`
    width: 30rem;
    height: 70%;
    border: 2px solid black;
    display: flex;
    flex-direction: column;
    align-items: center;
    div{
        width: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        input{
            width: 300px;
            height: 30px;
            ::placeholder{
                text-align: center;
                font-size: 1.5rem;
            }
        }
        button{
            width: 150px;
            height: 30px;
            margin:10px 0;
            font-size: 1rem;
        }
    }
    section{
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100%;
        height: 50%;
        img{
            width: 70%;
            height: 1000%;

        }
    }
`;
