import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { RandonUserApi } from '../../routes/constants';
import { ContainerBase } from '../../style';
import { ContainerPage, ContainerAction, ContainerSearch } from './style';

export default function FirstPage() {

   const [renderUser, setRenderUser] = useState([])
   const [inputSearch, setInputSearch] = useState('')
   const navigate = useNavigate('')
   let page = 1
   useEffect(()=>{ 
      axios
         .get('https://randomuser.me/api/?page=${page + 1}&results=5&seed=abc')
         .then((resp)=>{
            setRenderUser(resp.data.results) 
            console.log(resp.data);
         })
   },[page])

   const nextPage = ()=>{
      navigate('/secondPage')
   }

   const nextResults = () =>{
      page += 1
   }


 return (
   <ContainerBase>
      <ContainerSearch>
         <input
            placeholder='Pesquisar'
            value={inputSearch}
            onChange={(ev)=>{ setInputSearch(ev.target.value)}}
         />
         <button>Anterior</button>
         <button onClick={nextResults}>Proxima</button>
         <button onClick={nextPage}>Avancar</button>
      </ContainerSearch>
      <ContainerPage>
         {renderUser.map((user, key)=>{
            return(
               <div key={key}>
                  <img src={user.picture.large}></img>
                  <span>nome: {user.name.first} {user.name.last}</span>
                  <span>email: {user.email}</span>
                  <span>nome de usuario: {user.login.username}</span>
                  <span>idade: {user.dob.age} anos</span>
               </div>
            )
         })}
      </ContainerPage>
   </ContainerBase>
 );
}
