import styled from 'styled-components';

export const ContainerLogin = styled.div`
    width: 25rem;
    height: 30vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    form{
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        input{
            width: 300px;
            height: 25px;
            margin: 10px 0;
            outline: 0;
            border: none;
            background: none;
            :hover{
                border-bottom: 1px solid green;
            }
            :focus{
                border-bottom: 1px solid green;
            }
            ::placeholder{
                text-align: center;
                font-size: 1rem;
                font-weight: 800;
            }
        }
        button{
            width: 100px;
            height: 25px;
            background: green;
            border: none;
            border-radius: 10px;
            font-size: 1rem;
        }
    }
`;
