import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import Header from '../../components/header/header';
import { GlobalContext } from '../../context/globalStateContext';
import useRequestData from '../../hooks/useRequestdata';
import { ContainerBase } from '../../style';
import { ContainerList, ContainerPage, ContainerEachClient, ClientInfo, ClientAction,ContainerAdd } from './style';
import { URL_BASE } from '../../routes/constants';
import { useNavigate } from 'react-router-dom';
import  CardEachClient  from '../../components/eachClient/eachClient';
import { EachClient } from '../../components/eachClient/style';

export default function FourPage() {
  const navigate = useNavigate('')
  const [data, page, setPage] = useRequestData(`${URL_BASE}`)

  const removeClient = (id) => {
    axios
    .delete(`http://localhost:3003/clients/${id}`)
    .then((resp)=>{ 
      alert('Cliente deletado com sucesso...')
      setPage(!page)
    })
}

const updateClient = (id) =>{
  localStorage.setItem('idClient', id)
  navigate('/updateClient')
}

  const renderClient = data && data.clients.map((client, key)=>{
    return(
      // <ContainerEachClient key={key}>
      //           <ClientInfo>
      //             <div>
      //               <span>NOME: {client.name}</span>
      //               <span>CPF: {client.cpf}</span>
      //               <span>TELEFONE: {client.phone}</span>
      //               <span>EMAIL: {client.email}</span>
      //               <span>ENDERECO: {client.address}</span>
      //             </div>
      //           </ClientInfo>
      //           <ClientAction>
      //             <button onClick={()=>{updateClient(client._id)}}>E</button>
      //             <button onClick={()=>{removeClient(client._id)}}>X</button>
      //           </ClientAction>
      //         </ContainerEachClient>
      <tr>
        <td>{client.name}</td>
        <td>{client.name}</td>
        <td>{client.name}</td>
        <td>{client.name}</td>
        <td>{client.name}</td>
        <td>
          <button onClick={()=>{updateClient(client._id)}}>E</button>
          <button onClick={()=>{removeClient(client._id)}}>X</button>
        </td>
      </tr>
    )
  })
 return (
   <ContainerBase>
        <Header/>
        <ContainerPage>
          <ContainerList>
            <table border={1}>
              <tr>
                <th>NOME</th>
                <th>CPF</th>
                <th>EMAIL</th>
                <th>TELEFONE</th>
                <th>ENDERECO</th>
                <th>ACAO</th>
              </tr>
            {renderClient}
            </table>
          </ContainerList>
        </ContainerPage>
   </ContainerBase>
 );

}
